CREATE TABLE `gym_manager`.`user` ( `id` INT NOT NULL AUTO_INCREMENT , `date` VARCHAR(8) NOT NULL , `first_name` VARCHAR(15) NOT NULL , `last_name` VARCHAR(15) NOT NULL , `genre` VARCHAR(6) NOT NULL , `passport_number` VARCHAR(15) NOT NULL , `birth_date` VARCHAR(10) NOT NULL , `address` VARCHAR(50) NOT NULL , `city` VARCHAR(15) NOT NULL , `phone` VARCHAR(15) NOT NULL , `email` VARCHAR(30) NOT NULL , `status` VARCHAR(10) NOT NULL DEFAULT 'active' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
